definition.script:
pipeline {
    agent {
      label 'Slave'
    }
    tools {
        // Install the Maven version configured as "M3" and add it to the path.
        maven "M3"
    }
    environment {
        IMAGE = readMavenPom().getArtifactId()
        VERSION = readMavenPom().getVersion()
    }

    stages {
        stage('Checkout GIT') {
            steps {
                git branch: 'selenium_grid', url: 'https://github.com/przemaj1990/panda_aplication2.git'
            }
        }

        stage('Clear running apps') {
            steps {
                // Run Maven on a Unix agent.
                sh "docker rm -f pandaapp || true"
            }
        }
        stage('Build and Junit') {
            steps {
                // Run Maven on a Unix agent.
                sh "mvn clean install"
            }
        }
        stage('Build Docker image'){
            steps {
                sh "mvn package -Pdocker"
            }
        }

        //instalacja apki z Dockerfila na git
        stage('Run Docker app') {
            steps {
                sh "docker run -d -p 0.0.0.0:8080:8080 --name pandaapp -t ${IMAGE}:${VERSION}"
            }
        }
        stage('Test Selenium') {
            steps {
                sh "mvn test -Pselenium"
            }
        }
        stage('Deploy jar to artifactory') {
            steps {
                sh "echo deploy jar to artifactory"
                configFileProvider([configFile(fileId: '9d1ed313-ea70-4fa9-9934-7108c53eca75', variable: 'MAVEN_GLOBAL_SETTINGS')]) {
                    sh "mvn -gs $MAVEN_GLOBAL_SETTINGS deploy -Dmaven.test.skip=true -e"
                }
            }
        }

    }

            post {
                always { 
                    sh 'docker stop pandaapp'
                    deleteDir()
                }
            }
}
