definition.script:
pipeline {
    agent {
        label 'Slave'

    }

    tools {
        // Install the Maven version configured as "M3" and add it to the path.
        maven "M3"
    }

stages {
        stage('Git pull') {
            steps {
                // Get some code from a GitHub repository
                git branch: 'selenium_grid', url: 'https://github.com/przemaj1990/panda_aplication2.git'
            }
        }

    stage('mvn clean') {
            steps {
                // Run Maven on a Unix agent.
                sh "mvn clean install"
            }
        }

    stage('mvn test') {
            steps {
                // Run Maven on a Unix agent.
                sh "mvn test -Pselenium"
            }

        }
    }
}
